{
  "name": "Specific DateTime Reminder",
  "description": "Fires at a specific date and time",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "This is your scheduled reminder!"
  },
  "when": {
    "type": "at",
    "datetime": "2025-12-25 10:00",
    "timezone": "UTC"
  }
}

{
  "name": "30 Minute Reminder",
  "description": "Fires 30 minutes from creation",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "30 minutes have passed!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 30,
      "unit": "mins"
    }
  }
}

{
  "name": "2 Hour Reminder",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "2 hours have passed!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 2,
      "unit": "hours"
    }
  }
}

{
  "name": "7 Day Reminder",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "A week has passed!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 7,
      "unit": "days"
    }
  }
}

{
  "name": "Inactivity Reminder (30 mins)",
  "description": "Fires after 30 minutes of inactivity",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "We noticed you've been away. Need any help?"
  },
  "when": {
    "type": "after_inactivity",
    "duration": {
      "value": 30,
      "unit": "mins"
    }
  }
}

{
  "name": "Inactivity Reminder (2 hours)",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "text",
    "value": "We miss you! Come back and continue where you left off."
  },
  "when": {
    "type": "after_inactivity",
    "duration": {
      "value": 2,
      "unit": "hours"
    }
  }
}

{
  "name": "Natural Language Reminder",
  "description": "Uses natural language parsing for timing",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Time for your afternoon check-in!"
  },
  "when": {
    "type": "natural",
    "expression": "tomorrow at 3pm",
    "timezone": "America/New_York"
  }
}

{
  "name": "Natural Language - In 1 Hour",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "One hour reminder!"
  },
  "when": {
    "type": "natural",
    "expression": "in 1 hour",
    "timezone": "UTC"
  }
}

{
  "name": "Recurring Every 2 Hours",
  "description": "Fires every 2 hours starting now",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Time for your bi-hourly check-in!"
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 2,
      "unit": "hours"
    },
    "start": "now"
  }
}

{
  "name": "Recurring Hourly (start in 30 mins)",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Hourly reminder!"
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "hours"
    },
    "start": {
      "in": {
        "value": 30,
        "unit": "mins"
      }
    }
  }
}

{
  "name": "Daily Morning Reminder",
  "description": "Fires every day at 9:00 AM",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "text",
    "value": "Good morning! Here's your daily reminder."
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "days"
    },
    "at": "09:00",
    "timezone": "America/New_York"
  }
}

{
  "name": "Every 3 Days Reminder",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Your tri-daily reminder!"
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 3,
      "unit": "days"
    },
    "at": "14:30",
    "timezone": "UTC"
  }
}

{
  "name": "Mon Wed Fri Reminder",
  "description": "Fires on Monday, Wednesday, and Friday at 10:00 AM",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "text",
    "value": "It's a check-in day!"
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "weeks",
      "on": [1, 3, 5]
    },
    "at": "10:00",
    "timezone": "America/New_York"
  }
}

{
  "name": "Weekday Morning Reminder",
  "description": "Fires every weekday at 8:00 AM",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "all"
  },
  "content": {
    "type": "text",
    "value": "Good morning! Start your day right."
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "weeks",
      "on": [1, 2, 3, 4, 5]
    },
    "at": "08:00",
    "timezone": "UTC"
  }
}

{
  "name": "Single Session Reminder",
  "description": "Targets a specific session by ID",
  "target": {
    "type": "session",
    "session_id": "{{sessionId}}"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "This reminder is just for you!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "Multiple Sessions Reminder",
  "description": "Targets multiple specific sessions",
  "target": {
    "type": "sessions",
    "session_ids": ["session-id-1", "session-id-2", "session-id-3"]
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Group reminder for selected users!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 10,
      "unit": "mins"
    }
  }
}

{
  "name": "Broadcast Reminder",
  "description": "Targets all sessions in the workspace",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "This is a broadcast to everyone!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 15,
      "unit": "mins"
    }
  }
}

{
  "name": "Broadcast with Exclusions",
  "description": "Targets all sessions except specified ones",
  "target": {
    "type": "all_sessions",
    "exclude": ["session-to-exclude-1", "session-to-exclude-2"]
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Broadcast to most users!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 20,
      "unit": "mins"
    }
  }
}

{
  "name": "Text Content Reminder",
  "description": "Uses direct text content",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "This is a direct text message that will be sent exactly as written."
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "AI-Generated Reminder",
  "description": "Uses AI to generate the reminder message",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "prompt",
    "value": "Generate a friendly and motivational reminder to check in on their progress. Keep it brief and encouraging, under 50 words."
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "Browser Notification",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "This will show in the browser!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "Email Notification",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "text",
    "value": "This will be sent via email!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "Instagram DM Notification",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "instagram"
  },
  "content": {
    "type": "text",
    "value": "This will be sent via Instagram DM!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "name": "Multi-Channel Notification",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "all"
  },
  "content": {
    "type": "text",
    "value": "This will be sent through all available channels!"
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  }
}

{
  "reason": "No longer needed"
}

{
  "name": "Updated Reminder Name"
}

{
  "content": {
    "type": "text",
    "value": "Updated reminder message!"
  }
}

{
  "when": {
    "type": "in",
    "duration": {
      "value": 60,
      "unit": "mins"
    }
  }
}

{
  "name": "Auto-Apply Welcome Reminder",
  "description": "Automatically applies to new sessions",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "current"
  },
  "content": {
    "type": "text",
    "value": "Welcome! Let us know if you need any help."
  },
  "when": {
    "type": "in",
    "duration": {
      "value": 5,
      "unit": "mins"
    }
  },
  "autoApply": {
    "enabled": true,
    "apply_to_new_sessions": true
  }
}

{
  "name": "Daily AI Newsletter",
  "description": "Daily email with AI-generated content",
  "target": {
    "type": "all_sessions"
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "prompt",
    "value": "Generate a brief, engaging daily tip about productivity. Keep it under 100 words and include an actionable suggestion."
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "days"
    },
    "at": "08:00",
    "timezone": "America/New_York"
  },
  "autoApply": {
    "enabled": true,
    "apply_to_new_sessions": true
  }
}

{
  "name": "Personal Follow-up",
  "description": "Personalized follow-up after inactivity",
  "target": {
    "type": "session",
    "session_id": "{{sessionId}}"
  },
  "delivery": {
    "channel": "all"
  },
  "content": {
    "type": "prompt",
    "value": "Generate a warm, personalized check-in message for someone who hasn't engaged in a while. Be friendly and offer help without being pushy."
  },
  "when": {
    "type": "after_inactivity",
    "duration": {
      "value": 24,
      "unit": "hours"
    }
  }
}

{
  "name": "Weekly Team Update",
  "description": "Weekly update for non-admin users",
  "target": {
    "type": "all_sessions",
    "exclude": ["admin-session-1", "admin-session-2"]
  },
  "delivery": {
    "channel": "email"
  },
  "content": {
    "type": "text",
    "value": "Here's your weekly progress summary! Keep up the great work."
  },
  "when": {
    "type": "recurring",
    "interval": {
      "every": 1,
      "unit": "weeks",
      "on": [5]
    },
    "at": "17:00",
    "timezone": "America/New_York"
  }
}
